import React from 'react';
import { Container, Table, Badge } from 'react-bootstrap';
import Dashboard from '../components/dashboard';

const taskData = [
  {
    id: 1,
    task: 'Implement login functionality',
    assignee: 'Alice',
    status: 'To Do',
  },
  {
    id: 2,
    task: 'Design dashboard layout',
    assignee: 'Bob',
    status: 'In Progress',
  },
  {
    id: 3,
    task: 'Set up database schema',
    assignee: 'Charlie',
    status: 'Complete',
  },
  // Add more tasks as needed
];

const BacklogUser = () => {
  return (
    <Dashboard>
      <Container fluid className="p-4">
        <h2 className="mb-4">User Backlog</h2>
        <Table striped bordered hover responsive>
          <thead className="table-dark">
            <tr>
              <th>#</th>
              <th>Task &amp; Assignee</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {taskData.map(task => (
              <tr key={task.id}>
                {/* Task Number */}
                <td>{`TSK ${task.id}`}</td>
                
                {/* Task description and assignee */}
                <td>
                  <div>{task.task}</div>
                  <div className="mt-1">
                    <Badge bg="info">{task.assignee}</Badge>
                  </div>
                </td>
                
                {/* Task Status */}
                <td>
                  {task.status === 'To Do' && (
                    <Badge bg="warning" text="dark">
                      To Do
                    </Badge>
                  )}
                  {task.status === 'In Progress' && (
                    <Badge bg="primary">In Progress</Badge>
                  )}
                  {task.status === 'Complete' && (
                    <Badge bg="success">Complete</Badge>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </Container>
    </Dashboard>
  );
};

export default BacklogUser;
