import React, { useState } from 'react';
import { Container, Table, Modal, Form, Button, Badge } from 'react-bootstrap';
import Dashboard from '../components/dashboard';

// Initial dummy tasks with Priority and Due Date
const initialTasks = [
  {
    id: 1,
    task: 'Implement login functionality',
    assignee: 'Alice',
    status: 'To Do',
    priority: 'High',
    dueDate: '2025-03-15'
  },
  {
    id: 2,
    task: 'Design dashboard layout',
    assignee: 'Bob',
    status: 'In Progress',
    priority: 'Medium',
    dueDate: '2025-03-20'
  },
  {
    id: 3,
    task: 'Set up database schema',
    assignee: 'Charlie',
    status: 'Complete',
    priority: 'Low',
    dueDate: '2025-03-10'
  },
];

// Helper: Returns a color based on the status
function getStatusColor(status) {
  switch (status) {
    case 'To Do':
      return '#ff9800'; // orange
    case 'In Progress':
      return '#2196f3'; // blue
    case 'Complete':
      return '#4caf50'; // green
    default:
      return '#999';
  }
}

export default function BacklogUser() {
  const [tasks, setTasks] = useState(initialTasks);
  const [showModal, setShowModal] = useState(false);
  // New task form â€“ note no status field (defaults to "To Do")
  const [newTask, setNewTask] = useState({ task: '', assignee: '', priority: 'Low', dueDate: '' });

  // Open and close modal functions
  const openModal = () => setShowModal(true);
  const closeModal = () => {
    setShowModal(false);
    setNewTask({ task: '', assignee: '', priority: 'Low', dueDate: '' });
  };

  // Handle modal form changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewTask(prev => ({ ...prev, [name]: value }));
  };

  // Add new task (status defaults to "To Do")
  const addTask = () => {
    const newId = tasks.length ? tasks[tasks.length - 1].id + 1 : 1;
    setTasks([...tasks, { id: newId, ...newTask, status: 'To Do' }]);
    closeModal();
  };

  // Update status of an existing task
  const handleStatusChange = (id, newStatus) => {
    setTasks(tasks.map(task => task.id === id ? { ...task, status: newStatus } : task));
  };

  return (
    <Dashboard onAddTask={openModal}>
      <Container fluid className="p-4">
        <h2 className="mb-4">User Backlog</h2>
        <Table striped bordered hover responsive>
          <thead className="table-dark">
            <tr>
              <th>#</th>
              <th>Task &amp; Assignee</th>
              <th>Priority</th>
              <th>Due Date</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {tasks.map(task => (
              <tr key={task.id}>
                {/* Task Number */}
                <td>{`TSK ${task.id}`}</td>
                {/* Task description and assignee */}
                <td>
                  <div>{task.task}</div>
                  <div className="mt-1">
                    <Badge bg="info">{task.assignee}</Badge>
                  </div>
                </td>
                <td>{task.priority}</td>
                <td>{task.dueDate}</td>
                {/* Status with editable dropdown styled as a colored badge */}
                <td>
                  <Form.Select
                    value={task.status}
                    onChange={(e) => handleStatusChange(task.id, e.target.value)}
                    style={{
                      backgroundColor: getStatusColor(task.status),
                      color: '#fff',
                      fontWeight: 'bold',
                      border: 'none'
                    }}
                  >
                    <option value="To Do" style={{ backgroundColor: '#ff9800', color: '#fff' }}>To Do</option>
                    <option value="In Progress" style={{ backgroundColor: '#2196f3', color: '#fff' }}>In Progress</option>
                    <option value="Complete" style={{ backgroundColor: '#4caf50', color: '#fff' }}>Complete</option>
                  </Form.Select>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </Container>

      {/* Modal for adding a new task */}
      <Modal show={showModal} onHide={closeModal}>
        <Modal.Header closeButton>
          <Modal.Title>Add New Task</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="formTaskDescription" className="mb-3">
              <Form.Label>Task Description</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter task description"
                name="task"
                value={newTask.task}
                onChange={handleChange}
              />
            </Form.Group>
            <Form.Group controlId="formAssignee" className="mb-3">
              <Form.Label>Assignee</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter assignee name"
                name="assignee"
                value={newTask.assignee}
                onChange={handleChange}
              />
            </Form.Group>
            <Form.Group controlId="formPriority" className="mb-3">
              <Form.Label>Priority</Form.Label>
              <Form.Select
                name="priority"
                value={newTask.priority}
                onChange={handleChange}
              >
                <option value="Low">Low</option>
                <option value="Medium">Medium</option>
                <option value="High">High</option>
              </Form.Select>
            </Form.Group>
            <Form.Group controlId="formDueDate" className="mb-3">
              <Form.Label>Due Date</Form.Label>
              <Form.Control
                type="date"
                name="dueDate"
                value={newTask.dueDate}
                onChange={handleChange}
              />
            </Form.Group>
            <p className="text-muted">New tasks are marked as "To Do" by default.</p>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={closeModal}>
            Cancel
          </Button>
          <Button variant="primary" onClick={addTask}>
            Add Task
          </Button>
        </Modal.Footer>
      </Modal>
    </Dashboard>
  );
}
