import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Container, Row, Col, Button, InputGroup, FormControl } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { Outlet, Link } from "react-router-dom";

const Sidebar = () => {
    
  return (
    <Container fluid className="vh-100 d-flex flex-column">
      {/* Header */}
      <Row className="bg-light p-3 align-items-center">
        <Col md={3} className="fw-bold">PROJECT NAME</Col>
        <Col md={6}>
          <InputGroup>
            <FormControl placeholder="SEARCH BAR" />
          </InputGroup>
        </Col>
        <Col md={3} className="d-flex justify-content-end align-items-center">
          <Button variant="primary" className="me-3">ADD A TASK</Button>
          <div className="me-3 position-relative">
            <i className="bi bi-gear-fill fs-4"></i>
            
          </div>
          
        </Col>
      </Row>

      {/* Sidebar + Content */}
      <Row className="flex-grow-1">
        {/* Sidebar */}
        <Col md={2} className="bg-light d-flex flex-column p-3">
          <Link to="/nestcafe/pages/ToDO" className="btn btn-light w-100 my-1 text-start">Summary</Link>
          <Link to="/" className="btn btn-light w-100 my-1 text-start">Backlog</Link>
          <Link to="/" className="btn btn-light w-100 my-1 text-start">Timeline</Link>
          <Link to="/" className="btn btn-light w-100 my-1 text-start">Board</Link>
          <Link to="/" className="btn btn-light w-100 my-1 text-start">Reports</Link>
          <Link to="/" className="btn btn-light w-100 my-1 text-start">Forms</Link>
          <Link to="/" className="btn btn-light w-100 my-1 text-start">Goals</Link>
        </Col>

        {/* Main Content */}
        <Col md={10} className="bg-white">
          {/* Content goes here */}
        </Col>
      </Row>
    </Container>
  );
};

export default Sidebar;
