import React, { useState } from 'react';
import Header from '../components/header';
import UserForm from '../components/form';
import Buttonbrn from '../components/button';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';

const Login = () => {
  // State to control modal visibility
  const [showModal, setShowModal] = useState(false);

  return (
    <>        
      <Header title={"Login"} curPage={"Login"}/>
      <Container className='login-container'>
        <Row>
          <Col lg={4}></Col>
          <Col className='login-form' lg={4}>
            <div className='image' id="loginlogo">
              <img src="/images/nestcafelogolong.png?raw=true" alt="Company logo" className='rounded' id='loginlogo'/>
            </div>            
            <UserForm/>            
            <div className='buttons-area'>
              <div className='row' id='btn-row2'>
                <div className='left col-lg-6'>
                  <Buttonbrn title={"Forgot password"}/>
                </div>
                <div className='right col-lg-6'>
                  {/* Updated button to open modal */}
                  <Buttonbrn title={"Privacy policy"} link="#" onClick={() => setShowModal(true)} />
                </div>
              </div>
            </div>
          </Col>
          <Col lg={4}></Col>
        </Row>
      </Container>    

      {/* Privacy Policy Modal */}
      <Modal show={showModal} onHide={() => setShowModal(false)} centered>
        <Modal.Header closeButton>
          <Modal.Title>Privacy Policy</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>This is the Privacy Policy of NestCafe. We respect your privacy and ensure your data is protected.</p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default Login;
