import React from 'react';
import { Container, Table, Badge } from 'react-bootstrap';
import Dashboard from '../components/dashboard';

// Example tasks with start/end dates
const tasks = [
  {
    id: 1,
    name: 'SCRUM M1 - Find Deadline',
    status: 'DONE',
    start: '2025-03-27',
    end: '2025-03-28',
  },
  {
    id: 2,
    name: 'SCRUM M1 - Meeting 1',
    status: 'IN PROGRESS',
    start: '2025-03-28',
    end: '2025-04-01',
  },
  {
    id: 3,
    name: 'SCRUM M2 - Meeting 2',
    status: 'IN PROGRESS',
    start: '2025-04-02',
    end: '2025-04-05',
  },
  {
    id: 4,
    name: 'SCRUM M2 - Meeting 1 Date Change',
    status: 'TO DO',
    start: '2025-04-06',
    end: '2025-04-07',
  },
  {
    id: 5,
    name: 'SCRUM M3 - Meeting 1',
    status: 'TO DO',
    start: '2025-04-09',
    end: '2025-04-10',
  },
];

// Define the timeline range (keep it modest to avoid performance issues)
const startDate = new Date('2025-03-27');
const endDate = new Date('2025-04-15');

// Generate an array of dates (one per day) from startDate to endDate inclusive
function generateDateRange(start, end) {
  const dateArray = [];
  let currentDate = new Date(start);
  while (currentDate <= end) {
    dateArray.push(new Date(currentDate));
    currentDate.setDate(currentDate.getDate() + 1);
  }
  return dateArray;
}

let dateRange = generateDateRange(startDate, endDate);

// Limit the number of days (columns) to avoid excessive rendering
const maxDays = 30;
if (dateRange.length > maxDays) {
  dateRange = dateRange.slice(0, maxDays);
}

// Helper functions
function isWithinRange(date, start, end) {
  return date >= start && date <= end;
}

function parseDate(str) {
  return new Date(str + 'T00:00:00'); // Prevent timezone issues
}

function formatDate(date) {
  const day = String(date.getDate()).padStart(2, '0');
  const month = String(date.getMonth() + 1).padStart(2, '0');
  return `${day}/${month}`;
}

function getStatusColor(status) {
  switch (status) {
    case 'DONE':
      return '#4caf50';
    case 'IN PROGRESS':
      return '#2196f3';
    case 'TO DO':
      return '#ff9800';
    default:
      return '#999';
  }
}

export default function TimelinePage() {
  return (
    <Dashboard>
      <Container fluid className="p-4">
        <h2 className="mb-4">Timeline Page</h2>
        <Table bordered hover responsive>
          <thead className="table-light">
            <tr>
              {/* Left columns for Task Name and Status */}
              <th style={{ minWidth: '250px' }}>Sprint / Task</th>
              <th style={{ minWidth: '100px' }}>Status</th>
              {/* One column per day */}
              {dateRange.map((date) => (
                <th key={date.toISOString()} className="text-center">
                  {formatDate(date)}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {tasks.map((task) => {
              const taskStart = parseDate(task.start);
              const taskEnd = parseDate(task.end);
              const bgColor = getStatusColor(task.status);
              return (
                <tr key={task.id}>
                  <td>{task.name}</td>
                  <td>
                    <Badge
                      style={{
                        backgroundColor: bgColor,
                        color: '#fff',
                        fontWeight: 'normal',
                      }}
                    >
                      {task.status}
                    </Badge>
                  </td>
                  {dateRange.map((date) => {
                    const inRange = isWithinRange(date, taskStart, taskEnd);
                    return (
                      <td
                        key={date.toISOString()}
                        style={{
                          backgroundColor: inRange ? bgColor : 'transparent',
                          opacity: inRange ? 0.6 : 1,
                          transition: 'background-color 0.3s',
                        }}
                      />
                    );
                  })}
                </tr>
              );
            })}
          </tbody>
        </Table>
      </Container>
    </Dashboard>
  );
}
