import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Container, Row, Col, Button, InputGroup, FormControl } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { Outlet, Link } from "react-router-dom";

const Sidebar = ({children}) => {
    
    return (
        <Container fluid className="vh-100 d-flex flex-column">
          {/* Header */}
          <Row className="bg-light p-3 align-items-center">
            <Col md={3} className="fw-bold">PROJECT NAME</Col>
            <Col md={6}>
              <InputGroup>
                <FormControl placeholder="SEARCH BAR" />
              </InputGroup>
            </Col>
            <Col md={3} className="d-flex justify-content-end align-items-center">
              <Button variant="primary" className="me-3">ADD A TASK</Button>
              <div className="me-3 position-relative">
                {/* Example icon (gear) */}
                <i className="bi bi-gear-fill fs-4"></i>
                <span className="position-absolute top-0 start-100 translate-middle badge bg-danger">
                  1
                </span>
              </div>
              <div className="text-center">
                {/* Example icon (person) */}
                <i className="bi bi-person-circle fs-3"></i>
                <p className="m-0">ACBOT</p>
              </div>
            </Col>
          </Row>
    
          {/* Sidebar + Content */}
          <Row className="flex-grow-1">
            {/* Sidebar */}
            <Col md={2} className="bg-light d-flex flex-column p-3">
              <Button variant="light" className="w-100 my-1 text-start">Summary</Button>
              <Button variant="light" className="w-100 my-1 text-start">Backlog</Button>
              <Button variant="light" className="w-100 my-1 text-start">Timeline</Button>
              <Button variant="light" className="w-100 my-1 text-start">Board</Button>
              <Button variant="light" className="w-100 my-1 text-start">Reports</Button>
              <Button variant="light" className="w-100 my-1 text-start">Forms</Button>
              <Button variant="light" className="w-100 my-1 text-start">Goals</Button>
            </Col>
    
            {/* Main Content Area */}
            <Col md={10} className="bg-white p-4">
              {children}
            </Col>
          </Row>
        </Container>
      );
};

export default Sidebar;
